'use strict'

const fs = require('fs')

class logs {

    constructor (path)
    {
        this.path = path
    }
    
    check_exist () {
        fs.access(this.path, fs.constants.F_OK, (err) => {
            console.log(`Le fichier ${this.path} ${err ? "n'existe pas" : "existe"}`)
        })
    }

    addData (text) {
        fs.readFile(this.path, 'utf8', (err, data) => {
            fs.writeFile(this.path, `${data ? data : ''}${text}\n`, (err) => {
                err ? console.log(err) : '';  
            })
        }) 
    }

    create_file () {
        fs.appendFile(this.path, '', (err) => {
            err ? console.log(err) : ''
        })
    }
    

}

module.exports = logs